--
-- Table structure for table `dokter`
--

CREATE TABLE `dokter` (
  `id` int(10) NOT NULL,
  `NamaDokter` varchar(100) NOT NULL,
  `gambar` varchar(100) DEFAULT NULL,
  `spesailis` varchar(100) NOT NULL,
  `profilesingkat` mediumtext NOT NULL,
  `pendidikan` varchar(500) NOT NULL,
  `organisasi` mediumtext NOT NULL,
  `pelayanan` longtext NOT NULL,
  `tempatpraktik` varchar(200) NOT NULL,
  `alamatpraktik` varchar(200) NOT NULL,
  `jadwalpraktik` varchar(50) NOT NULL,
  `jampraktik` varchar(50) NOT NULL,
  `biaya` varchar(16) NOT NULL,
  `nohp` varchar(16) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

--
-- Dumping data for table `dokter`
--

INSERT INTO `dokter` (`id`, `NamaDokter`, `gambar`, `spesailis`, `profilesingkat`, `pendidikan`, `organisasi`, `pelayanan`, `tempatpraktik`, `alamatpraktik`, `jadwalpraktik`, `jampraktik`, `biaya`, `nohp`) VALUES
(31, 'dr. Cahyani Kusuma Dewi', 'dr-cahyani.jpg', 'UMUM', 'dr. Cahyani Kusuma Dewi merupakan seorang Dokter Umum yang memiliki pengalaman selama 16 tahun di bidangnya. Saat ini, dr. Cahyani Kusuma Dewi berpraktek di Klinik Puri Medika Kartika Chandra di Pancoran, Jakarta Selatan, dan Klinik Pola di Pasar Minggu, Jakarta Selatan.', '', 'Ikatan Dokter Indonesia', '', 'Klinik Puri Medika Kartika Chandra', 'Jl. Gatot Subroto Kav 18-20. 12710, Jakarta Selatan - Pancoran, Jakarta Selatan, Jakarta, Indonesia 12710', 'SENIN - SELASA, KAMIS - JUMAT', '09:00-12:00, 13:00-15:30', '75.000', '+6285283395666'),
(32, 'dr. Yessi Astrica Gunawan, Dipl. AAAM', 'dr-yessi-astrica-gunawan-dipl-aaam1.600x800.jpg', 'UMUM', 'dr. Yessi Astrica Gunawan merupakan seorang Dokter Umum yang memiliki pengalaman selama 10 tahun di bidangnya. Saat ini, dr. Yessi Astrica Gunawan berpraktek di Genese Aesthetic Clinic Penjaringan, Jakarta Utara. Sebagai seorang dokter, beliau telah mengenyam pendidikan di Universitas Tarumanagara, dan American Academy of Aesthetic Medicine.', 'S.Ked. - Sarjana Kedokteran - Universitas Tarumanagara, 2010', 'Ikatan Dokter Indonesia', '<li>Penyakit Umum</li>\r\n<li>Konsultasi Kesehatan</li>\r\n<li>Konsultasi Umum</li>\r\n<li>Pemeriksaan Fisik</li>\r\n<li>Akupunktur</li>\r\n<li>Skincare & Slimming</li>\r\n<li>Chemical Peeling</li>\r\n<li>Bedah Umum</li>\r\n<li>Jerawat</li>\r\n<li>Facial</li>\r\n<li>Tanam Benang (Thread Lift)</li>\r\n', 'Genese Aesthetic Clinic', 'Jl. Pluit Karang Sari Blok Q8 no. 57 - Penjaringan, Jakarta Utara, Jakarta, Indonesia 14450', 'SENIN - SABTU', '09:30-20:00', 'N/A', '+622166675608'),
(33, 'drg. Johannes Yohnson Rachmat', 'drg-johannes-yohnson-rachmat.600x800.jpg', 'DOKTER GIGI', 'drg. Johannes Yohnson Rachmat merupakan seorang Dokter Gigi Umum yang memiliki pengalaman selama 39 tahun di bidangnya. Saat ini, drg. Johannes Yohnson Rachmat berpraktek di Praktek drg. Johannes Rachmat Kebon Jeruk, Jakarta Barat. Sebagai seorang dokter, beliau telah mengenyam pendidikan di Universitas Trisakti.', '1. S.K.G. - Sarjana Kedokteran Gigi - Universitas Trisakti<br>\r\n2. drg. - Kedokteran Gigi - Universi', '\r\nPersatuan Dokter Gigi Indonesia', '<li>Perawatan Gigi</li>\r\n<li>Crown And Bridges (Mahkota Gigi Tiruan & Jembatan Gigi)</li>\r\n<li>Pemasangan Kawat Gigi</li>\r\n<li>Gigi Tiruan Cekat (GTC)</li>\r\n<li>Gigi Tiruan Sebagian (GTS)</li>\r\n<li>Pencabutan Gigi</li>', 'Dokter Gigi Praktek Perorangan', 'Jl. Surya Barat Blok D No. 22,Sunrise Garden - Kebon Jeruk, Jakarta Barat, Jakarta, Indonesia 11520', 'SENIN - JUMAT', '17:00-20:00', '50.000', '+62215802289'),
(34, 'Dr. dr. Taufik Jamaan, Sp.OG ', 'dr-taufik-jamaan-spog.600x800.jpg', 'UMUM', 'dr. Taufik Jamaan merupakan seorang Dokter Spesialis Kebidanan dan Kandungan yang memiliki pengalaman selama 22 tahun di bidangnya. Saat ini, dr. Taufik Jamaan berpraktek di Rumah Sakit Ibu dan Anak Bunda di Menteng, Jakarta Pusat, dan Rumah Sakit Metropolitan Medical Center di Setiabudi, Jakarta Selatan. Sebagai seorang dokter, beliau telah mengenyam pendidikan di Universitas Indonesia.', 'dr. - Kedokteran Umum - Universitas Indonesia, 1991\r\nSp.OG - Spesialis Obstetri & Gine', 'Ikatan Dokter Indonesia\r\ndan  Perhimpunan Obstetri dan Ginekologi Indonesia', '<li>KB</li>\r\n<li>Tes Kehamilan</li>\r\n<li>Konsultasi Kehamilan\r\n <li>USG</li>\r\n <li>Persalinan</li>\r\n<li>USG</li>\r\n<li>Persalinan</li>', 'Rumah Sakit Ibu dan Anak Bunda', 'Jl. Teuku Cik Ditiro No.12 - Menteng, Jakarta Pusat, Jakarta, Indonesia 10350', 'SENIN - SABTU', '09:00-13:00', 'Rp.Rp.N/A', '+622131922005'),
(35, 'dr. Dickson Tan', 'dr-dickson-tan.600x800.jpg', 'UMUM', 'dr. Dickson Tan merupakan seorang Dokter Umum yang memiliki pengalaman selama 10 tahun di bidangnya. Saat ini, dr. Dickson Tan berpraktek di Praktek dr. Dickson Tan Cengkareng, Jakarta Barat. Sebagai seorang dokter, beliau telah mengenyam pendidikan di Universitas Katolik Indonesia Atma Jaya. dr. Dickson Tan merupakan anggota dari Ikatan Dokter Indonesia. Adapun layanan kesehatan yang diberikan oleh dr. Dickson Tan adalah Imunisasi, Penyakit Umum, Check Up Kesehatan, Penyakit Menular, Imunisasi Anak, dan BPJS.', 'Dr. - Universitas Katolik Indonesia Atma Jaya', 'Ikatan Dokter Indonesia (IDI)', '<li>Imunisasi Penyakit Umum</li> <li>Check Up Kesehatan</li> <li>Penyakit Menular</li> <li>Imunisasi Anak</li> <li>BPJS</li>', 'Praktek dr. Dickson Tan', 'Jl. Sakura No. 4 - Cengkareng, Jakarta Barat, Jakarta, Indonesia 11730', 'SENIN - SABTU', '08:00-12:00, 16:00-21:00', 'Rp. 50000', '+6281291263511'),
(36, 'dr. Musinggih Djarot Roujani, Sp.KJ', 'dr samuel oetoro sp.gk.jpg', 'Psikiater', 'dr. Musinggih Djarot Roujani, Sp.KJ adalah seorang Dokter Psikiater. Saat ini, beliau berpraktik di Rumah Sakit JIH, Yogyakarta. Adapun layanan medis yang dapat beliau berikan meliputi, Konsultasi perihal kesehatan jiwa. dr. Musinggih Djarot Roujani, Sp.KJ menempuh pendidikan Dokter umum, lulus pada tahun 1972, kemudian melanjutkan pendidikan Spesialis Kedokteran Jiwa, lulus pada tahun 1985, di Universitas Gadjah Mada. Setelah itu beliau mengikuti Kursus Brain Mapping di USA, pada tahun 1993. Beliau juga tercatat sebagai anggota dari Ikatan Dokter Indonesia (IDI) dan Perhimpunan Dokter Spesialis Kedokteran Jiwa Indonesia (PDSKJI).', 'dr. - Kedokteran Umum - Universitas Gadjah Mada, 1972\r\nSp.KJ - Spesialis Kedokteran Jiwa - Universitas Gadjah Mada, 1985\r\nKursus Brain Mapping - USA, 1993\r\n', 'Ikatan Dokter Indonesia (IDI)\r\nPerhimpunan Dokter Spesialis Kedokteran Jiwa Indonesia (PDSKJI).\r\n', 'Konsultasi perihal kesehatan jiwa', 'Rumah Sakit JIH', 'Rumah Sakit JIH, Depok, Yogyakarta', 'SENIN – SABTU', '14:30 - 16:30', '125', 'N/A'),
(37, '\r\ndr. Tri Kumoroyekti, Sp.KJ', 'dr. Tri Kumoroyekti, Sp.KJ.png', 'Psikiater', 'dr. Tri Kumoroyekti, Sp.KJ merupakan Dokter Kejiwaan yang berpraktik di Rumah Sakit Edelweiss. Beliau dapat membantu layanan konsultasi kesehatan terkait kejiwaan. dr. Tri Kumoroyekti, Sp.KJ menyelesaikan pendidikan Kedokteran Jiwa di Universitas Padjadjaran. Selain itu, Beliau terhimpun dalam Ikatan Dokter Indonesia (IDI) dan Perhimpunan Dokter Spesialis Kedokteran Jiwa Indonesia (PDSKJI).', 'Sp.KJ - Spesialis Kedokteran Jiwa - Universitas Padjadjaran', 'Ikatan Dokter Indonesia (IDI)\r\nPerhimpunan Dokter Spesialis Kedokteran Jiwa Indonesia (PDSKJI).\r\n', 'Konsultasi perihal kesehatan jiwa', 'Rumah Sakit Edelweiss', 'Rumah Sakit Edelweiss, Buah Batu, Bandung', 'Selasa, Jumat, Sabtu', '14:00 - 17:30', 'Rp. 200.000', 'N/A'),
(38, 'dr. I Yuwanda Chrissander, Sp.GK', 'dr. I Yuwanda Chrissander, Sp.GK.jpg', 'Dokter Gizi', 'dr. I Yuwanda Chrissander, Sp.GK merupakan seorang Dokter Gizi Klinik yang berpraktik di Eka Hospital Bekasi. Beliau dapat membantu layanan Konsultasi keseimbangan gizi. dr. I Yuwanda Chrissander, Sp.GK menamatkan pendidikan Dokter Spesialis Gizi Klinik di Universitas Hasanuddin. Selain itu, beliau juga terhimpun dalam organisasi Perhimpunan Dokter Spesialis Gizi Klinik Indonesia (PDGKI).', 'Sp.GK - Spesialis Gizi Klinik - Universitas Hasanuddin\r\ndr. - Kedokteran Umum - Universitas Trisakti', 'Perhimpunan Dokter Spesialis Gizi Klinik Indonesia (PDGKI).', 'Konsultasi keseimbangan gizi', 'Rumah Sakit JIH, Yogyakarta', 'Rumah Sakit JIH, Depok, Yogyakarta', 'Senin - Sabtu', '07:00 - 16:00', 'Rp. 125.000', 'N/A'),
(40, 'Dr. dr. Samuel Oetoro, MS, Sp.GK (K)', 'dr samuel oetoro sp.gk.jpg', 'Dokter Gizi', 'Dr. dr. Samuel Oetoro, MS, Sp.GK (K) adalah seorang Dokter Gizi Klinik yang berpraktek di MRCCC Siloam Hospitals Semanggi dan Mandaya Royal Hospital Puri. Beliau menamatkan pendidikan Spesialis Gizi Klinik dari Universitas Indonesia. dr. Samuel Oetoro merupakan anggota dari Ikatan Dokter Indonesia dan Perhimpunan Dokter Spesialis Gizi Klinik Indonesia. Layanan yang beliau berikan meliputi : Konsultasi Keseimbangan Gizi.', 'Sp.GK - Spesialis Gizi Klinik - Universitas Indonesia', 'Ikatan Dokter Indonesia(IDI)\r\nPerhimpunan Dokter Spesialis Gizi Klinik Indonesia (PDGKI).', 'Konsultasi keseimbangan gizi', 'MRCCC Siloam Hospitals Semanggi', 'MRCCC Siloam Hospitals Semanggi, Setiabudi, Jakarta', 'Senin, Rabu, Jumat, Sabtu', '14:00 - 19:00', 'Rp. 500.000', 'N/A'),
(41, 'dr. Felix Candra Sutanto, Sp.A', 'dr. Felix Candra Sutanto, Sp.A1.jpg', 'Dokter Anak', 'dr. Felix Candra Sutanto, Sp.A merupakan seorang Dokter Anak. Beliau dapat membantu layanan Konsultasi kesehatan anak menyeluruh. dr. Felix Candra Sutanto menamatkan pendidikan Spesialis Anak di Universitas Sam Ratulangi, Manado. Selain itu, beliau juga terhimpun dalam organisasi Ikatan Dokter Indonesia (IDI) dan Ikatan Dokter Anak Indonesia (IDAI).', 'Sp.A - Spesialis Anak - Universitas Sam Ratulangi, Manado\r\ndr. - Kedokteran Umum - Universitas Brawijaya, Malang\r\n', 'Ikatan Dokter Indonesia (IDI)\r\nIkatan Dokter Anak Indonesia (IDAI).\r\n', 'Konsultasi kesehatan anak menyeluruh', 'Eka Hospital Bekasi', 'Eka Hospital Bekasi, Bekasi Utara, Bekasi', 'Senin, Selasa, Kamis, Jumat', '08:00-12:00, 16:00-21:00', 'Rp.310.000', 'N/A'),
(42, 'dr. Yoke Kinanthi Putri, Sp.A, IBCLC', 'dr. Yoke Kinanthi Putri, Sp.A, IBCLC.jpg', 'Dokter Anak', 'dr. Yoke Kinanthi Putri, Sp.A, IBCLC merupakan seorang Dokter Anak. Beliau dapat membantu layanan Konsultasi kesehatan anak menyeluruh. dr. Yoke Kinanthi Putri menamatkan pendidikan Spesialis Anak di Universitas Gadjah Mada. Selain itu, beliau juga terhimpun dalam organisasi Ikatan Dokter Indonesia (IDI) dan Ikatan Dokter Anak Indonesia (IDAI).', 'dr. - Kedokteran Umum - Universitas Gadjah Mada\r\nSp.A - Spesialis Anak - Universitas Gadjah Mada\r\nMagister Ilmu Kedokteran Klinis - Universitas Gadjah Mada\r\n\r\n', 'Ikatan Dokter Indonesia (IDI)\r\nIkatan Dokter Anak Indonesia (IDAI).\r\n', 'Konsultasi kesehatan anak menyeluruh', 'Eka Hospital Bekasi', 'Eka Hospital Bekasi, Bekasi Utara, Bekasi', 'Senin - Sabtu', '08:00-12:00, 15:00-20:00', 'Rp.310.000', 'N/A'),
(43, 'dr. Ferry Ferdian Nugraha', 'dr-ferry-ferdian-nugraha.600x800.jpg', 'UMUM', 'dr. Ferry Ferdian Nugraha merupakan seorang Dokter Umum yang memiliki pengalaman selama 13 tahun di bidangnya. Saat ini, dr. Ferry Ferdian Nugraha berpraktek di In House Clinic - Hotel Le Grandeur Mangga Dua di Sawah Besar, Jakarta Pusat, dan In House Clinic - Hotel Ritz Carlton Pacific Place di Kebayoran Baru, Jakarta Selatan. Sebagai seorang dokter, beliau telah mengenyam pendidikan di Universitas Kristen Krida Wacana, dan Universitas Indonesia. dr. Ferry Ferdian Nugraha merupakan anggota dari Ikatan Dokter Indonesia. Adapun layanan kesehatan yang diberikan oleh dr. Ferry Ferdian Nugraha adalah Kesehatan Anak Dan Dewasa, Penyakit Umum, Konsultasi Umum, Pengobatan Umum, dan Pemeriksaan Fisik.', 'Universitas Kristen Krida Wacana, dan Universitas Indonesia.', 'Ikatan Dokter Indonesia (IDI).', '<li>Kesehatan Anak Dan Dewasa</li>\r\n<li>Penyakit Umum</li>\r\n<li>Konsultasi Umum</li>\r\n<li>Pengobatan Umum</li>\r\n<li>Pemeriksaan Fisik</li>\r\n', 'In House Clinic - Hotel Le Grandeur Mangga Dua\r\nKlinik', 'Jl. Mangga Dua Raya - Sawah Besar, Jakarta Pusat, Jakarta, Indonesia 10730', 'KAMIS', '15:00-18:00', 'N/A', '+62216128811'),
(44, 'dr. Anne Rivaida, M.Gizi, Sp.GK', 'dr. Anne Rivaida, M.Gizi, Sp.GK.jpg', 'Dokter Gizi', 'dr. Anne Rivaida, M.Gizi, Sp.GK adalah seorang Dokter Gizi Klinik yang berpraktek di Siloam Sriwijaya Palembang dan Charitas Hospital Palembang. Beliau dapat membantu layanan Konsultasi Keseimbangan Gizi. dr. Anne Rivaida, M.Gizi, Sp.GK menyelesaikan pendidikan Spesialis Gizi Klinik di Pengukuhan Kolegium Ilmu Gizi Klinik Indonesia. Selain itu, beliau juga tercatat sebagai anggota Ikatan Dokter Indonesia (IDI) dan Perhimpunan Dokter Gizi Medik Indonesia (PDGMI).', 'Sp.GK - Spesialis Gizi Klinik - Pengukuhan Kolegium Ilmu Gizi Klinik Indonesia', 'Ikatan Dokter Indonesia (IDI)\r\nPerhimpunan Dokter Spesialis Gizi Medika Indonesia (PDGMI).\r\n', 'Konsultasi keseimbangan gizi', 'Siloam Sriwijaya Palembang', 'Siloam Sriwijaya Palembang, Ilir Barat I, Palembang', 'Selasa, Kamis', '15:30 - 16:15', 'Rp. 150.000', 'N/A');

